INSERT INTO Product(ProductId, SellerId, Category, Title, Price, Description, ImageUrl, ProductUrl, IsQuote, IsVisible) VALUES (11, 6, 'Service', 'Auto Body, Collision Repair, and Paint', null, 'Auto body, collision repair, and paint services are designed for motorists who need assistance after a vehicle receives damage from an accident or is recovered from a theft. After a collision, the resulting vehicular damage can be seen in the form of dents and scratches on the vehicle’s body and blemishes in its paint. In addition, a vehicle’s wires may need to be fixed, or its parts may need to be replaced after a theft or accident occurs. Depending upon the severity of the damage, prices for auto body, collision repair, and paint services will vary. Before we begin work on a damaged vehicle, our automotive repair and paint staff can provide owners with a collision or theft repair estimate. Once we reach an agreement, our experts will tend to the vehicle’s damage. Ultimately, our goal is to restore vehicles, both internally and externally, to their pre-accident or pre-theft condition.', 'https://s3.amazonaws.com/mechanic.checker/product/Screenshot+2021-03-20+155104.png', 'https://www.mymechanicauto.ca/Auto-Repair/cat/auto-body-collision-repair-and-paint', 1, 1);
INSERT INTO Product(ProductId, SellerId, Category, Title, Price, Description, ImageUrl, ProductUrl, IsQuote, IsVisible) VALUES (12, 6, 'Service', 'Axle, CV Joint & Driveshaft Repair', null, 'Your vehicle’s axle, CV joint, and driveshaft components are integral to the performance and drivability of your vehicle. The driveshaft is a steel or aluminum cylindrical component that connects the transmission to the rear differential. The driveshaft connects to the transmission through the CV joints, which are responsible for transferring the torque from the transmission to the drive wheels at a constant speed. Front wheel drive vehicles use CV joints on the front axles. CV joints allow the axle to bend and change while accommodating the bounce of the wheels. The axle bears the weight of the vehicle while also being strong enough to sustain the forces from braking and accelerating. Over time and through normal use, your vehicle’s axle, CV joint, and driveshaft components may require maintenance or replacement services.  ', 'https://s3.amazonaws.com/mechanic.checker/product/Screenshot+2021-03-20+160017.png', 'https://www.mymechanicauto.ca/Auto-Repair/cat/axle-cv-joint-driveshaft-repair', 1, 1);
